export type DSInput = {
    model: string;
    messages: Array<Message>;
    temperature?: number;
    top_p?: number;
    tools?: Array<{
        type: string;
        function: {
            name: string;
            description: string;
            parameters: object;
        };
    }>;
};
type Message = SystemMessage | UserMessage | AssistantMessage | ToolMessage;
type SystemMessage = {
    content: string;
    role: 'system';
};
type UserMessage = {
    content: string;
    role: 'user';
};
type ToolMessage = {
    content: string;
    role: 'tool';
};
type AssistantMessage = {
    role: 'assistant';
    content: string;
} | {
    role: 'assistant';
    tool_calls: Array<ToolCall>;
    content?: string;
};
type ToolCall = {
    id: string;
    type: string;
    function: {
        name: string;
        arguments: string;
    };
};
export type DSGenerateOutput = {
    choices: Array<{
        finish_reason: string;
        message: {
            content: string;
            role: 'assistant';
            tool_calls?: Array<ToolCall>;
        };
    }>;
    usage: {
        prompt_tokens: number;
        completion_tokens: number;
        total_tokens: number;
    };
};
export type DSStreamOutput = {
    choices: Array<{
        delta: {
            content: string;
            tool_calls?: Array<ToolCall>;
            role?: 'assistant';
        };
        finish_reason: string;
    }>;
    usage?: {
        prompt_tokens: number;
        completion_tokens: number;
        total_tokens: number;
    };
};
export {};
