import { ChatModelMessage } from '../../type';
type SystemMessage = {
    role: 'system';
    content: string;
};
type UserMessage = {
    role: 'user';
    content: string;
};
type AssistantMessage = {
    role: 'assistant';
    content?: string;
    tool_calls?: Array<{
        id: string;
        type: string;
        function?: {
            name: string;
            arguments: string;
        };
    }>;
};
type ToolMessage = {
    role: 'tool';
    content: string;
    tool_call_id: string;
};
type ZhiPuMessage = SystemMessage | UserMessage | AssistantMessage | ToolMessage;
export type ZhiPuInputData = {
    model: string;
    messages: Array<ZhiPuMessage>;
    request_id?: string;
    do_sample?: boolean;
    temperature?: number;
    top_p?: number;
    max_tokens?: number;
    stop?: Array<string>;
    tools?: Array<{
        type: string;
        function: {
            name: string;
            description: string;
            parameters: object;
        };
    }>;
    tool_choice?: 'auto';
    user_id?: string;
};
export type ZhiPuGenerateTextOutput = {
    choices?: Array<{
        finish_reason?: 'stop' | 'tool_calls' | 'length' | 'sensitive' | 'network_error';
        message?: ChatModelMessage;
    }>;
    usage?: {
        completion_tokens: number;
        prompt_tokens: number;
        total_tokens: number;
    };
};
export type ZhiPuStreamTextOutput = {
    choices?: Array<{
        index: number;
        finish_reason?: 'stop' | 'tool_calls' | 'length' | 'sensitive' | 'network_error';
        delta?: ChatModelMessage;
    }>;
    usage?: {
        completion_tokens: number;
        prompt_tokens: number;
        total_tokens: number;
    };
};
export {};
