export interface ICreateFunctionAgentParams {
    /**
     * 智能体名称
     * @example "hello"
     */
    Name: string;
    /**
     * 智能体ID
     * @example "ibot-aaa-bbb"
     */
    BotId: string;
    /**
     * 智能体简介
     * @example "这是一个智能体"
     */
    Introduction?: string;
    /**
     * 智能体头像
     * @example "https://picsum.photos/200"
     */
    Avatar?: string;
}
